mapper.select:
SELECT 
FROM XXL_JOB_QRTZ_TRIGGER_LOG AS t

	
		AND t.job_group = #{jobGroup}
	
	
		AND t.job_id = #{jobId}
	
	
		AND t.trigger_time  >=  #{triggerTimeStart}
	
	
		AND t.trigger_time  <=  #{triggerTimeEnd}
	
	and t.trigger_code!=600 
	and t.handle_code!=666
	
		AND t.handle_code = 200
	
	
		AND (
			t.trigger_code NOT IN (0, 200) OR
			t.handle_code NOT IN (0, 200,433)
		)
	
	
		AND t.trigger_code = 200
		AND t.handle_code = 0
	
	
		AND (t.trigger_code = 200 AND t.handle_code=433)
	
	
		AND t.parent_id in(
		
			#{parentId}
		)
	

ORDER BY id DESC
LIMIT #{offset}, #{pagesize}

SELECT count(1)
FROM XXL_JOB_QRTZ_TRIGGER_LOG AS t

	
		AND t.job_group = #{jobGroup}
	
	
		AND t.job_id = #{jobId}
	
	
		AND t.trigger_time  >=  #{triggerTimeStart}
	
	
		AND t.trigger_time  <=  #{triggerTimeEnd}
	
	and t.trigger_code!=600 
	and t.handle_code!=666
	
		AND t.handle_code = 200
	
	
		AND (
			t.trigger_code NOT IN (0, 200) OR
			t.handle_code NOT IN (0, 200,433)
		)
	
	
		AND (t.trigger_code = 200 AND t.handle_code=0)
	
	
		AND (t.trigger_code = 200 AND t.handle_code=433)
	
	
		AND t.parent_id in(
		
			#{parentId}
		)

SELECT 
FROM XXL_JOB_QRTZ_TRIGGER_LOG AS t
WHERE t.id = #{id}

SELECT count(1)
FROM XXL_JOB_QRTZ_TRIGGER_LOG AS t

	
		AND t.handle_code = #{handleCode}

mapper.delete:
delete from XXL_JOB_QRTZ_TRIGGER_LOG

	
		AND job_group = #{jobGroup}
	
	
		AND job_id = #{jobId}
	
	
		AND trigger_time  <=  #{clearBeforeTime}
	
	
		AND id NOT in(
			SELECT id FROM(
				SELECT id FROM XXL_JOB_QRTZ_TRIGGER_LOG AS t
				
					
						AND t.job_group = #{jobGroup}
					
					
						AND t.job_id = #{jobId}
					
				
				ORDER BY t.trigger_time desc
				LIMIT 0, #{clearBeforeNum}
			) t1
		)
